}

{

  function common_end(strs) {

  	// Find minimum length of file name

  	ls = [];
    whole = [];
	for (i=0;i<strs.length;i++){
  		ls = ls.concat(strs[i].path.split('/').pop().length);
        whole = whole.concat(strs[i].path.length);
  	}
 	l = Math.min.apply(Math,ls);

  	// Loop from the end through characters and check if they are the same for all

 	ind = 0;
 	flag = 0;
 	for (i=1;i<=l;i++){
    	for (j=1;j<strs.length;j++){
      		if (strs[0].path[whole[0]-i]!=strs[j].path[whole[j]-i]){
        	flag = 1;
        	break
      		}
   		}
   	 	if (flag == 1){
   	 	  	break
   	    } else {
      		ind = ind + 1;
        }
  	 }

  // Assign and return longest ending common substring
  if (ind>0) {
  	comstr = strs[0].path.slice(-ind);
  } else {
    comstr = 'different_extensions'
  }

  return comstr

  }

  if ($job.inputs.bams instanceof Array) { // VK
    if ($job.inputs.bams[0] instanceof Array) {

      // Support for input received as list of one-element-lists 
      in_var = []
      for (i=0;i<$job.inputs.bams.length;i++) 
  		in_var = in_var.concat($job.inputs.bams[i]);

    } else {
      in_var = [].concat($job.inputs.bams)
    }


  } else {
    in_var = [].concat($job.inputs.bams)
  }

  prefix=''

  if(in_var.length==1) { 
    return '' // Input will be just passed to output
  }else if (in_var[0].metadata){
 	   if ("sample_id" in in_var[0].metadata){   
  		 prefix = in_var[0].metadata["sample_id"];      
   	    } else {
   		  prefix = 'sample_unknown';
        }

  }else {
    prefix = 'sample_unknown';   
  }

  // Create joint name and add the merged suffix
  joint_name = prefix + '_' + common_end(in_var);
  name = joint_name.split('.').slice(0,-1).join('.') + '.merged.bam'



  return name

}

