fileDef.fileContent:
"""
Usage:
    sbg_fastq_quality_scale_adjuster.py --fastq FILE

Options:
    -h, --help          Show this message.

    -f, --fastq FILE    Input FASTQ file.

"""

from docopt import docopt
import os
import gzip
import itertools as it
import shutil
import sys
from math import log10
from subprocess import Popen



args = docopt(__doc__, version='1.0')

input_file = args['--fastq'] 

base_name = input_file[input_file.rfind('/')+1:input_file.rfind('.') if input_file.rfind('.') != -1 else None]
r_ext = input_file[input_file.rfind('.')+1:] if input_file.rfind('.') else ""
l_ext = base_name.split('.')[-1].lower()
if l_ext == 'fastq' or l_ext == 'fq':
    if not r_ext == 'fastq' and not r_ext == 'fq':
        base_name = base_name[:base_name.rfind('.')]
output_file = base_name + '.std.fastq'


"""input and output names defined above"""

class myGzipFile(gzip.GzipFile):
    def __enter__(self, *args, **kwargs):
        if self.fileobj is None:
            raise ValueError("I/O operation on closed GzipFile object")
        return self

    def __exit__(self, *args, **kwargs):
        self.close()


def extremes(a, b):
    if a is False:
        return b, b
    return min(a[0], b), max(a[1], b)


def walk_qualities(f, sample_size=1000):
    for i in xrange(sample_size * 4):
        try:
            line = f.next()
        except StopIteration:
            return
        if i % 4 == 3:
            yield line.rstrip("\n\r")


def sniff(path):
    with open(path, 'rb') as f:
        gz = f.read(2) == '\x1f\x8b'
    opn = myGzipFile if gz else open
    with opn(path) as f:
        return get_scale(*map(ord, reduce(extremes, it.chain(*walk_qualities(f)), False)))


def get_scale(ord_min, ord_max):
    options = {
        'illumina13': (64, 105),
        'illumina15': (66, 105),
        'sanger': (33, 126),
        'solexa': (59, 105),
    }
    fits = [(k, v) for k, v in options.iteritems() if v[0] <= ord_min and v[1] >= ord_max]
    if not fits:
        message = 'Quality scale for range (%s, %s) not found.' % (ord_min, ord_max)
        raise Exception(message)
        # Return narrowest range
    return reduce(lambda a, b: a if a[1][1] - a[1][0] < b[1][1] - b[1][0] else b, fits)[0]

def qsolexa(x):
    return chr(int(round(10 * log10(10.0**((ord(x)-64)/10.0)+1))) + 33)


def qillumina13(x):
    return chr(ord(x) - 31)


def qillumina15(x):
    return chr(ord(x) - 31) if ord(x)-64 > 2 else chr(33)


def qillumina18(x):
    return x


"""detect quality scale format"""

meta_qual = sniff(input_file)


"""adjust quality scale if needed"""

if meta_qual == 'illumina13':
    proc = qillumina13
elif meta_qual == 'illumina15':
    proc = qillumina15
elif meta_qual == 'solexa':
    proc = qsolexa
else:
    proc = None

if proc == qsolexa: 
    with open(input_file, 'rb') as f:
        gz = f.read(2) == '\x1f\x8b'
    open_gz = myGzipFile if gz else open
    with open(output_file, 'w') as out:
        for i, line in enumerate(open_gz(input_file)):
            if i % 4 == 3:
                line = line.strip()
                converted = ''.join(map(proc, line))
                out.write(converted + '\n')
            else:
                out.write(line)
    contents = "Original fastq quality scale format was " + meta_qual + ", and is converted to illumina18.\n"
elif proc is not None: #seqtk converter from illumina13-15
    with open(input_file, 'rb') as f:
        gz = f.read(2) == '\x1f\x8b'
    f.close()
    if gz:#gunzip file to temp.fastq
        temp_file = 'temp.fastq'

        inF = gzip.GzipFile(input_file, 'rb')
        outF = file(temp_file, 'wb')

        newline = ''
        for lines in inF:
            outF.write(newline + lines.rstrip('\n'))
            newline = '\n'

        inF.close()
        outF.close()

    else:
        temp_file = input_file

    cmd = ['seqtk','seq','-Q64','-V',temp_file,'>',output_file]
    with open(output_file, 'w') as out:
        p = Popen(cmd, stdout = out)
        p.communicate()
    contents = "Original fastq quality scale format was " + meta_qual + ", and is converted to illumina18.\n"
else:
    if input_file.rfind(".gz") == len(input_file) - 3:
        output_file = output_file + ".gz"
    #shutil.copyfile(input_file, output_file)
    #os.symlink(input_file, output_file)
    os.rename(input_file, output_file)
    contents = "Original fastq quality scale format was illumina18. No conversion performed.\n"

sys.stderr.write(contents) #Write conversion to error log

