{ 
    /** Extracts filter name from expression */
    function getVariableName(filterExpression){
        var expressions = ['!=', '==', '>=', '<=', '>', '<']

        for (var i = 0; i < expressions.length; i++) {
            var indexOf = filterExpression.indexOf(expressions[i])

            if (indexOf >= 0)
                return filterExpression.slice(0, indexOf).trim()
        }

        return ''
    }

    /** Combines multiple filters into a new filter name */
    function getFilterName(filterExpressions){
        var newFilterName = ''

        for (var i = 0; i < filterExpressions.length; i++) {
            var variableName = getVariableName(filterExpressions[i])

            newFilterName = newFilterName ? (newFilterName + '-' + variableName) : variableName
        }

        return newFilterName
    }

    filterName = $job.inputs.filter_name
    filterExpressions = $job.inputs.filter_expression
    splitFilterExpressions = $job.inputs.split_filter_expressions

    // Adds filter expressions to the command line
    if (filterExpressions && filterExpressions.length > 0) 
    {  
        if (splitFilterExpressions) // Adds each expression as a separate filter
        {
            cmd = []

            for (i = 0; i < filterExpressions.length; i++) 
            {
                var variableName = getVariableName(filterExpressions[i])

                cmd.push('--filter-name')
                cmd.push('"' + variableName + '"')
                cmd.push('--filter-expression')
                cmd.push('"' + filterExpressions[i] + '"')
            }

            return cmd.join(' ')
        }
        else // Adds all expressions as a single filter
        { 
            filterName = filterName ? filterName : getFilterName(filterExpressions)

            var expressions = []

            for (var i = 0; i < filterExpressions.length; i++) 
            {
                expressions.push(filterExpressions[i])

                if (i < filterExpressions.length - 1)
                expressions.push('||')
            }

            expressions = expressions.join(' ').trim()
            expressions = '"' + expressions + '"'

            return(['--filter-name', 
                    '"' +  filterName + '"', 
                    '--filter-expression', 
                    expressions].join(' ').trim())   
        }
    }
    else
        return ''
}

