{
  function find_prefix(variants)
  {
    var prefix = ''
    var first = variants[0].path.replace(/^.*[\\\/]/, '')

    // Finds longest common prefix between variant names
    for (var i = 1; i < variants.length; ++i)
    {
      var j = 0;
      var current = variants[i].path.replace(/^.*[\\\/]/, '')

      while(first[j] == current[j] && 
            j < (first.length - 1) && 
            j < (current.length - 1)) ++j

      // Inits prefixs or shortens it
      if (i == 1 || prefix.length > j)
      	prefix = first.slice(0, j)
    }

    // Clips trailing characters
    while (prefix.endsWith('.') || prefix.endsWith('_') || prefix.endsWith('-'))
      prefix = prefix.slice(0, prefix.length - 1)

    return prefix
  }


  var variants = [].concat($job.inputs.input)

  if ($job.inputs.clip_intervals && variants.length > 1)
  {
    var first = variants[0].path.replace(/^.*[\\\/]/, '')

    var extensions = ''

    if (first.endsWith('.g.vcf'))
      extensions = '.g.vcf'
    else if (first.endsWith('.vcf'))
      extensions = '.vcf'
    else if (first.endsWith('.g.vcf.gz'))
      extensions = '.g.vcf.gz'
    else if (first.endsWith('.vcf.gz'))
      extensions = '.vcf.gz'

    var prefix = find_prefix(variants)

    if (prefix.length > 0)
      return prefix + extensions
    else 
      return variants[0].path.replace(/^.*[\\\/]/, '')
  }
  else
    return variants[0].path.replace(/^.*[\\\/]/, '')
}

